// hitung total
function HitungTotal() {
  const HargaMakanan = harga_makanan.innerText.replace('Rp. ', '');
  const HargaMinuman = harga_minuman.innerText.replace('Rp. ', '');
  const JumlahMakanan = parseInt(jumlah_makanan.value);
  const JumlahMinuman = parseInt(jumlah_minuman.value);
  const Total = (HargaMakanan * JumlahMakanan) + (HargaMinuman * JumlahMinuman);
  total_harga.innerHTML = '<span id="totalHarga">Total Harga : Rp. ' + Total + '</span>';

  if (bayar_pesanan.value != 0) {
    HitungKembalian(Total);
  }
}

// hitung sisa kembali
function HitungKembalian(Total) {
  const Bayar = parseInt(bayar_pesanan.value);
  const Kembalian = Bayar - Total;
  sisa_kembalian.innerHTML = '<h5> Rp. ' + Kembalian + '</h5>';
}

// fuction button
document.querySelector('form').addEventListener('submit', function (event) {
  event.preventDefault();
  HitungTotal();
});

function hitungVolume() {
  const Tinggi = parseInt(tinggi.value);
  const Lebar = parseInt(lebar.value);
  const Panjang = parseInt(panjang.value);

  // Menghitung luas dan volume
  const volume = Panjang * Lebar * Tinggi;
  // Menampilkan hasil
  document.getElementById('volume').textContent = volume;

  return false;
}

function hitungLuas() {
  const LebarLuas = parseInt(lebarluas.value);
  const PanjangLuas = parseInt(panjangluas.value);

  // Menghitung luas dan volume
  const luas = PanjangLuas * LebarLuas;
  // Menampilkan hasil
  document.getElementById('luas').textContent = luas;

  return false;
}

