function convertTemperature(value, unit) {
  if (isNaN(value)) return;

  let celsius, fahrenheit, reaumur, kelvin;

  switch (unit) {
    case 'celsius':
      celsius = parseFloat(value);
      fahrenheit = celsius * 9 / 5 + 32;
      reaumur = celsius * 4 / 5;
      kelvin = celsius + 273.15;
      break;
    case 'fahrenheit':
      fahrenheit = parseFloat(value);
      celsius = (fahrenheit - 32) * 5 / 9;
      reaumur = celsius * 4 / 5;
      kelvin = celsius + 273.15;
      break;
    case 'reaumur':
      reaumur = parseFloat(value);
      celsius = reaumur * 5 / 4;
      fahrenheit = celsius * 9 / 5 + 32;
      kelvin = celsius + 273.15;
      break;
    case 'kelvin':
      kelvin = parseFloat(value);
      celsius = kelvin - 273.15;
      fahrenheit = celsius * 9 / 5 + 32;
      reaumur = celsius * 4 / 5;
      break;
    default:
      break;
  }

  document.getElementById('celsius').value = celsius;
  document.getElementById('fahrenheit').value = fahrenheit;
  document.getElementById('reaumur').value = reaumur;
  document.getElementById('kelvin').value = kelvin;
}


function openHitung(evt, hitungName) {
  // Declare all variables
  var i, tabs, tablinks;

  // Get all elements with class="tabs" and hide them
  tabs = document.getElementsByClassName("tabs");
  for (i = 0; i < tabs.length; i++) {
    tabs[i].style.display = "none";
  }

  // Get all elements with class="tablinks" and remove the class "active"
  tablinks = document.getElementsByClassName("tablinks");
  for (i = 0; i < tablinks.length; i++) {
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }

  // Show the current tab, and add an "active" class to the button that opened the tab
  document.getElementById(hitungName).style.display = "block";
  evt.currentTarget.className += " active";
} 